mapper.select:
SELECT f.partySeq, f.id, f.title, f.content, f.image, f.wdate, f.del, b.readcount, f.tag, a.likey, f.hate FROM partyBbs f LEFT JOIN (SELECT bbsSeq, IFNULL(SUM(likeCheck),0) AS likey FROM partylikey GROUP BY bbsSeq) a ON f.partySeq = a.bbsSeq LEFT JOIN (SELECT bbsSeq, IFNULL(COUNT(*),0) AS readcount FROM partyread GROUP BY bbsSeq) b ON f.partySeq = b.bbsSeq WHERE 1=1
AND (
f.title LIKE CONCAT('%', #{search}, '%')
f.content LIKE CONCAT('%', #{search}, '%')
f.id = #{search}
) AND f.tag = #{tag}
AND (
f.title LIKE CONCAT('%', #{search}, '%')
f.content LIKE CONCAT('%', #{search}, '%')
f.id = #{search}
)
AND f.tag = #{tag}
AND f.tag = #{tag}
AND ( f.title LIKE CONCAT('%', #{search}, '%') OR f.content LIKE CONCAT('%', #{search}, '%') ) AND f.tag = #{tag}
ORDER BY f.partySeq DESC LIMIT #{start}, 20

