AND (
f.title LIKE CONCAT('%', #{search}, '%')
f.content LIKE CONCAT('%', #{search}, '%')
f.id = #{search}
) AND f.tag = #{tag}
AND (
f.title LIKE CONCAT('%', #{search}, '%')
f.content LIKE CONCAT('%', #{search}, '%')
f.id = #{search}
)
AND f.tag = #{tag}
