mapper.select:
select id, title, content, image, wdate, del, readcount, tag, likey, hate from (select row_number()over(order by wdate desc) as rnum, id, title, content, image, wdate, del, readcount, tag, likey, hate from freeBbs where 1=1 AND id =#{id}
and title like concat('%', #{search}, '%') and del =0
and content like concat('%', #{search}, '%') and del =0
and id=#{search} and del =0
order by wdate desc) a
where id =#{id} AND rnum between ${start} and ${end}

SELECT f.bbsSeq, f.id, f.title, f.content, f.image, f.wdate, f.del, b.readcount, f.tag, a.likey, f.hate FROM freebbs f LEFT JOIN (SELECT bbsSeq, IFNULL(SUM(likeCheck),0) AS likey FROM likey GROUP BY bbsSeq) a ON f.bbsSeq = a.bbsSeq LEFT JOIN (SELECT bbsSeq, IFNULL(COUNT(*),0) AS readcount FROM readcount GROUP BY bbsSeq) b ON f.bbsSeq = b.bbsSeq WHERE 1=1
AND (
f.title LIKE CONCAT('%', #{search}, '%')
f.content LIKE CONCAT('%', #{search}, '%')
f.id = #{search}
) AND f.tag = #{tag}
AND (
f.title LIKE CONCAT('%', #{search}, '%')
f.content LIKE CONCAT('%', #{search}, '%')
f.id = #{search}
)
AND f.tag = #{tag}
AND f.tag = #{tag}
AND ( f.title LIKE CONCAT('%', #{search}, '%') OR f.content LIKE CONCAT('%', #{search}, '%') ) AND f.tag = #{tag}
ORDER BY f.bbsSeq DESC LIMIT #{start}, 20

SELECT ifnull(COUNT(*),0)
FROM (
SELECT f.bbsSeq, f.id, f.title,
f.content, f.image, f.wdate, f.del,
f.readcount, f.tag, a.likey, f.hate
FROM freebbs f
LEFT JOIN (
SELECT bbsSeq, IFNULL(SUM(likeCheck),0) AS
likey
FROM likey
GROUP BY bbsSeq
) a ON f.bbsSeq = a.bbsSeq
WHERE 1=1

	AND (

		f.title LIKE CONCAT('%', #{search}, '%')


		f.content LIKE CONCAT('%', #{search}, '%')


		f.id = #{search}

	)
	AND f.tag = #{tag}



	AND (

		f.title LIKE CONCAT('%', #{search}, '%')


		f.content LIKE CONCAT('%', #{search}, '%')


		f.id = #{search}

	)



	AND f.tag = #{tag}



	AND f.tag = #{tag}



	AND (
	f.title LIKE CONCAT('%', #{search}, '%')
	OR f.content
	LIKE CONCAT('%', #{search}, '%')
	)
	AND f.tag = #{tag}

) AS result

SELECT
ifnull(sum(likeCheck),0) from likey where bbsSeq=#{bbsSeq} and
memSeq=#{memSeq}
