mapper.select:
select * from (select @rownum:=@rownum+1 as rnum, partySeq, id, title, content, image, wdate, del, readcount, tag, partyType, people, likey, hate, place, mdate, applyMem, endParty, fullParty from partyBbs, (SELECT @rownum:=0) r where 1=1 and del =0
and title like concat('%', #{search}, '%')
and content like concat('%', #{search}, '%')
and id=#{search}
and tag=#{tag}
order by wdate desc) a
where rnum between #{start} and #{end}

select * from (select @rownum:=@rownum+1 as rnum, partySeq, id, title, content, image, wdate, del, readcount, tag, partyType, people, likey, hate, place, mdate, applyMem, endParty, fullParty from partyBbs , (SELECT @rownum:=0) r where 1=1 AND id =#{id}
and title like concat('%', #{search}, '%') and del =0
and content like concat('%', #{search}, '%') and del =0
and id=#{search} and del =0
order by wdate desc) a
where id =#{id} AND rnum between #{start} and #{end}

