mapper.select:
select partySeq, applyMem, `check`, wdate, title, full, masterId from (select row_number()over(order by wdate desc) as rnum, partySeq, applyMem, `check`, wdate, title, full, masterId from apply where 1=1 AND applyMem =#{id} AND masterId !=#{id}
order by wdate desc) a
where applyMem =#{id} AND masterId !=#{id} AND rnum between ${start} and ${end}

select partySeq, applyMem, `check`, wdate, title, full, masterId, totalMem, countMem from (select row_number()over(order by wdate desc) as rnum, partySeq, applyMem, `check`, wdate, title, full, masterId, totalMem, countMem from apply where 1=1 AND applyMem !=#{id} AND masterId =#{id}
order by wdate desc) a
where applyMem !=#{id} AND masterId =#{id} AND rnum between ${start} and ${end}

select partySeq, applyMem, `check`, wdate, title, full, masterId from (select row_number()over(order by wdate desc) as rnum, partySeq, applyMem, `check`, wdate, title, full, masterId from apply where 1=1 AND (applyMem =#{id} OR (masterId = #{id} AND applyMem = #{id}))
order by wdate desc) a
where (applyMem =#{id} OR masterId = #{id}) AND `check` = 1 AND rnum between ${start} and ${end}
